def get_lexicographically_minimum_string(str):
    new = list(str)
    unique_chars = list(set(str))
    unique_chars.sort(reverse=True)
    for x in new:
        if x not in unique_chars:
            if x == unique_chars[-1]:
                unique_chars.pop()
            else:
                (item, repwith) = (x, unique_chars[-1])
                break
    else:
        return str
    for (y, x) in enumerate(new):
        if x == item:
            new[y] = repwith
        elif x == repwith:
            new[y] = item
    return ''.join(new)

