def min_operations_to_strictly_increasing(arr: list[int]) -> int:
    """"""
    n = len(arr)
    counter = 0
    for i in range(n - 2, -1, -1):
        if arr[i] >= arr[i + 1]:
            if arr[i + 1] == 0:
                return -1
            d = int(1 + log(arr[i] / arr[i + 1], 2))
            counter += d
            arr[i] >>= d
    return counter
