def count_paths_with_xor_k(n, m, k, grid):
    d = {}
    for i in range(22):
        d[i] = {}

    def check(x, y):
        return 0 <= x < n and 0 <= y < m

    def go(x, y, now):
        if not check(x, y):
            return
        now ^= grid[x][y]
        if x + y == m - 1:
            if now in d[x]:
                d[x][now] += 1
            else:
                d[x][now] = 1
            return
        go(x + 1, y, now)
        go(x, y + 1, now)

    def goRev(i, j, now):
        if not check(i, j):
            return
        if i + j == m - 1:
            cur = k ^ now
            if cur in d[i]:
                nonlocal ans
                ans += d[i][cur]
            return
        now ^= grid[i][j]
        goRev(i - 1, j, now)
        goRev(i, j - 1, now)
    ans = 1
    go(0, 0, 0)
    goRev(n - 1, m - 1, 0)
    return ans

