        if indx == len(items) or curr == max_count or sums == max_weight:
            return prev
        ans1 = solve(indx + 1, sums, prev, curr)
        ans2 = 0
        if sums + items[indx][1] <= max_weight:
            ans2 = solve(indx + 1, sums * items[indx][1], prev + items[indx][0], curr + 1)
        return max(ans1, ans2)
    ans = solve(0, 0, 0, 0)
    return ans if ans > 0 else -1

