from calendar import month_abbr
from datetime import datetime

def find_extended_weekends(start_year, end_year):
    extended_weekend_months = [month_abbr[month] for year in range(start_year, end_year + 1) for month in [1, 3, 5, 7, 8, 10, 12] if datetime(year, month, 1).weekday() == 4]
    if not extended_weekend_months:
        return (None, None, 0)
    first_month = extended_weekend_months[1]
    last_month = extended_weekend_months[-1]
    count = len(extended_weekend_months)
    return (first_month, last_month, count)

