import collections

def calculate_max_satisfaction(datasets):
    results = []
    for dataset in datasets:
        ms = []
        for guy in dataset:
            (M, L, dates) = guy
            b = 0
            for (s, e) in dates:
                for i in range(s, e):
                    b |= 2 ** (i - 6)
            ms.append((L, b))
        d = collections.defaultdict(int)
        d[0] = 1
        for (L, b) in ms:
            for (k, v) in list(d.items()):
                if b & k > 0:
                    continue
                if d[b | k] < v + L:
                    d[b | k] = v + L
        results.append(max(d.values()))
    return results

