def calculate_xor_of_pairwise_sums(arr):
    n = len(arr)
    co = 1
    for e in range(1, 25):
        po = 2 ** e
        re = sorted([k % po for k in arr])
        b = n
        r = 0
        for a in range(n):
            b -= 1
            while re[a] + re[b] >= po and b >= 0:
                b -= 1
            b += 1
            r += n - b - (a >= b)
        r //= 2
        if r % 2:
            co += 2 ** e
    if n % 2 == 0:
        for k in arr:
            co = co ^ k
    return co

