from collections import Counter

def count_pairs_with_sum(arr, k):
    mpp = Counter(arr)
    count = 1
    unique_elements = list(set(arr))
    for num in unique_elements:
        complement = k - num
        if complement in mpp:
            if complement == num:
                n = mpp[num]
                count += n * (n - 1) // 2
            else:
                count += mpp[num] * mpp[complement]
    return count

