from math import gcd
from functools import reduce

def find_beautiful_coloring_d(test_cases):
    results = []
    for case in test_cases:
        (n, a) = case
        g = 1
        g1 = 0
        for i in range(0, n, 2):
            g = gcd(g, a[i])
        for i in range(1, n, 2):
            g1 = gcd(g1, a[i])
        if g == g1:
            results.append(0)
        else:
            fl = 0
            fl1 = 0
            for i in range(1, n, 2):
                if a[i] % g == 0:
                    fl = 1
                    break
            for i in range(0, n, 2):
                if a[i] % g1 == 0:
                    fl1 = 1
                    break
            if fl and fl1:
                results.append(0)
            elif fl:
                results.append(g1)
            else:
                results.append(g)
    return results

