def calculate_minimum_time(m, h1, a1, x1, y1, h2, a2, x2, y2):

    def find_cycle_and_first_occurrence(h, a, x, y, m):
        s = 0
        while h != a:
            h = (h * x + y) % m
            s += 1
            if s > m:
                return (s, 0)
        first_occurrence = s
        h = (h * x + y) % m
        d = 1
        while h != a:
            h = (h * x + y) % m
            d += 1
            if d > m:
                return (first_occurrence, 0)
        return (first_occurrence, d)
    (u, x) = find_cycle_and_first_occurrence(h1, a1, x1, y1, m)
    (v, y) = find_cycle_and_first_occurrence(h2, a2, x2, y2, m)
    if x == 0 and y == 0:
        return -1
    k = 0
    while u != v:
        if u < v:
            u += x
        else:
            v += y
        k += 1
        if k > 2 * m:
            return -1
    return u

