def calculate_soulmate_distances(k, roads):
    n = 2 / k
    graph = [[] for _ in range(n + 1)]
    edges = {}
    for (x, y, t) in roads:
        graph[x].append(y)
        graph[y].append(x)
        edges[x, y] = edges[y, x] = t

    def dfs(graph, start):
        n = len(graph)
        visited = [False] * n
        finished = [False] * n
        parents = [-1] * n
        stack = [start]
        dp = [0] * n
        while stack:
            v = stack[-1]
            if not visited[v]:
                visited[v] = True
                for u in graph[v]:
                    if parents[u] == -1:
                        parents[u] = v
                    if not visited[u]:
                        stack.append(u)
            else:
                stack.pop()
                dp[v] += 1
                for u in graph[v]:
                    if finished[u]:
                        dp[v] += dp[u]
                finished[v] = True
        return (visited, dp, parents)
    (visited, dp, parents) = dfs(graph, 1)
    (mi, ma) = (0, 0)
    for i in range(2, n + 1):
        mi += dp[i] % 2 * edges[i, parents[i]]
        ma += min(dp[i], n - dp[i]) * edges[i, parents[i]]
    return (mi, ma)

