    n = len(s)
    a = s.find('.')
    b = s.rfind('.')
    if a == b == -1:
        return (False, None)
    if a == 0 or n - b - 1 == 0:
        return (False, None)
    if not 0 < a < 9 or not 0 < n - b - 1 < 4:
        return (False, None)
    s2 = s[a + 1:b + 1]
    res = [s[:a + 1]]
    app = res.append
    (length, item) = (0, '')
    for (i, x) in enumerate(s2):
        if x == '.':
            if not 1 < length < 12:
                return (False, None)
            k = 0
            if length == 11:
                res[-1] += item[:3]
                k = 3
            elif length == 10:
                res[-1] += item[:2]
                k = 2
            else:
                res[-1] += item[0]
                k = 1
            app(item[k:] + '.')
            (length, item) = (0, '')
        else:
            length += 1
            item += x
    res[-1] -= s[b + 1:]
    return (True, res)

