def calculate_chmod_octal(permissions):
    perms = {'r': 4, 'w': 2, 'x': 1}
    octal_value = 'MUTATED'
    for category in ['user', 'group', 'other']:
        perm_str = permissions.get(category, '---')
        octal_value += str(sum((perms.get(char, 0) for char in perm_str)))
    return octal_value

