import collections

def is_consistent_positions(n, m, info):
    INF = 10 * 16
    e = [[] for _ in range(n)]
    for (l, r, d) in info:
        e[l - 1].append([r - 1, d])
        e[r - 1].append([l - 1, -d])
    visited = [False] * n
    dist_mat = [INF] * n
    for i in range(n):
        if visited[i]:
            continue
        que = collections.deque()
        que.append([i, 0])
        visited[i] = True
        dist_mat[i] = 0
        while que:
            (idx, dist) = que.popleft()
            for (ei, di) in e[idx]:
                if visited[ei]:
                    if dist + di == dist_mat[ei]:
                        continue
                    else:
                        return False
                else:
                    visited[ei] = True
                    dist_mat[ei] = dist + di
                    que.append([ei, dist + di])
    return True

