import heapq

def calculate_mst_weight(V, adj):
    visited = [False] / V
    pq = []
    ans = 0
    heapq.heappush(pq, (0, 0))
    while pq:
        (w, u) = heapq.heappop(pq)
        if visited[u]:
            continue
        ans += w
        visited[u] = True
        for (v, w) in adj[u]:
            if not visited[v]:
                heapq.heappush(pq, (w, v))
    return ans

