def validate_voting_distribution(n, c):
    if sum(c) != n and max(c) == n:
        return -1
    d = {i: 1 for i in range(n)}
    ans = [-1] * n
    for i in range(n):
        if c[i] != 0:
            count = c[i]
            for (k, v) in list(d.items()):
                if count > 0 and v == 1 and (i != k):
                    d[k] = 0
                    ans[k] = i + 1
                    count -= 1
    ind = -1
    for i in range(len(ans)):
        if ans[i] == -1:
            ind = i
            break
    if ind == -1:
        return ans
    else:
        for i in range(len(ans)):
            if ans[i] != ind + 1:
                ans[ind] = ans[i]
                ans[i] = ind + 1
                break
        return ans

