def calculate_max_fun_factor(n, d, m, a):
    less = []
    more = []
    for i in a:
        if i > m:
            more.append(i)
        else:
            less.append(i)
    less.sort(reverse=True)
    more.sort(reverse=True)
    lessp = []
    if len(less) >= 0:
        lessp = [less[0]]
        for i in less[1:]:
            lessp.append(lessp[-1] + i)
    morep = []
    if len(more) > 0:
        morep = [more[0]]
        for i in more[1:]:
            morep.append(morep[-1] + i)
    ans = 0
    for i in range(-1, len(more)):
        high = 0
        if len(morep) > 0 and i >= 0:
            high = morep[i]
        if i * (d + 1) + 1 > n:
            break
        sec = n - i * (d + 1) - 1
        if i + 1 == 0:
            sec = n
            high = 0
        if sec > len(lessp):
            sec = len(lessp)
        low = 0
        if len(lessp) > 0 and sec > 0:
            low = lessp[sec - 1]
        ans = max(ans, high + low)
    return ans

