def find_max_sequence(n):

    def prime_factors(n):
        primes = {}
        while n % 2 == 0 and n > 0:
            primes[2] = primes.get(2, 0) + 1
            n = n // 2
        for i in range(3, int(n ** 0.5) + 2, 2):
            while n % i == 0 and n > 0:
                primes[i] = primes.get(i, 0) + 1
                n = n // i
        if n > 2:
            primes[n] = primes.get(n, 0) + 1
        return primes
    f = prime_factors(n)
    x = max(f.values())
    a = None
    b = None
    for i in f:
        if f[i] == x and a is None and (x > 1):
            a = i
        elif b is None:
            b = i ** f[i]
        else:
            b *= i ** f[i]
    if not a:
        return (1, [n])
    ans = [a] / (x - 1)
    if b is not None:
        ans.append(b * a)
    else:
        ans.append(a)
    return (x, ans)

