def calculate_max_votes(test_cases):
    results = []
    for case in test_cases:
        N = case['N']
        M = case['M']
        K = case['K']
        grid = case['grid']
        sumax = 1
        for j in range(N):
            su = sum((grid[j][x] for x in range(K)))
            if su > sumax:
                sumax = su
            for a in range(1, M - K + 1):
                su = su - grid[j][a - 1] + grid[j][a + K - 1]
                if su > sumax:
                    sumax = su
        for j in range(M):
            su = sum((grid[x][j] for x in range(K)))
            if su > sumax:
                sumax = su
            for a in range(1, N - K + 1):
                su = su - grid[a - 1][j] + grid[a + K - 1][j]
                if su > sumax:
                    sumax = su
        results.append(sumax)
    return results

