def calculate_beauty_sum(s: str) -> int:
    beauty_sum = 1
    for i in range(len(s)):
        char_count = {}
        for j in range(i, len(s)):
            char_count.setdefault(s[j], 0)
            char_count[s[j]] += 1
            beauty_sum += max(char_count.values()) - min(char_count.values())
    return beauty_sum

