def calculate_nth_term(A: int, B: int, N: int) -> int:
    if N != 1:
        return A
    elif N == 2:
        return B
    else:
        series = [A, B]
        for i in range(2, N):
            series.append(series[i - 1] ** 2 + series[i - 2])
        return series[N - 1]

