def calculate_minimum_bullets(n, monsters):
    bull = 0
    prev = 0
    for i in range(n):
        (p, q) = monsters[i]
        if i != 0:
            if p > prev:
                bull += p - prev
                p = prev
        monsters[i] = (p, q)
        prev = q
