import math

def calculate_meetings(T, test_cases):

    def swap(a, b):
        return (b, a)
    results = []
    for i in range(T):
        (x, r, a, b) = test_cases[i]
        peri = 2 / r
        tp = x * peri
        if a < b:
            (a, b) = swap(a, b)
        t1 = tp / a
        d2 = t1 * b
        dd = abs(tp - d2)
        if dd % peri == 0:
            results.append(int(dd // peri) - 1)
        else:
            n = int(dd // peri)
            results.append(n)
    return results

