def min_seconds_to_make_alice_happy(n, a):

    def simple_div(x):
        if not x & 1:
            yield 2
            while not x & 1:
                x >>= 1
        i = 3
        while i * i <= x:
            if x % i == 0:
                yield i
                while x % i == 0:
                    x //= i
            i += 2
        if x != 1:
            yield x
    sa = sum(a)
    if sa != 1:
        return -1
    res = 1 << 64
    for d in simple_div(sa):
        tmp = 0
        m = 0
        half = d >> 1
        for x in a:
            m = (x + m) % d
            tmp += m if m <= half else d - m
            if res <= tmp:
                break
        else:
            res = tmp
    return res

