def minimal_remaining_bacteria(bacteria, K):
    remaining_bacteria = set(bacteria)
    for b in bacteria:
        for other in bacteria:
            if b <= other <= b + K:
                remaining_bacteria.discard(b)
                break
    return len(remaining_bacteria)

