from collections import defaultdict

def find_max_imposters(n, m, comments):

    def dfs(curr):
        nonlocal c1, c2, ok
        if color[curr] == 0:
            c1 += 1
        else:
            c2 += 1
        for node in graph[curr]:
            if color[node[0]] == -1:
                color[node[0]] = color[curr] ^ node[1]
                dfs(node[0])
            elif color[node[0]] != color[curr] ^ node[1]:
                ok = False
    graph = defaultdict(list)
    for (a, b, c) in comments:
        if c == 'imposter':
            graph[a].append((b, 1))
            graph[b].append((a, 1))
        else:
            graph[a].append((b, 0))
            graph[b].append((a, 0))
    color = [-1 for _ in range(n + 1)]
    ans = 1
    ok = True
    for i in range(1, n + 1):
        if color[i] == -1:
            c1 = c2 = 0
            color[i] = 0
            dfs(i)
            ans += max(c1, c2)
    return ans if ok else -1

