def find_minimum_enlightenment(temple_positions, num_monks):

    def is_enlightenment_possible(ar, n, k, dist):
        pos = ar[0] + dist
        for i in ar:
            if abs(pos - i) > dist:
                k = k - 1
                pos = i + dist
        k = k - 1
        if k >= 0:
            return True
        return False
    temple_positions.sort()
    (lo, hi) = (1, 10000000)
    ans = 1
    while lo <= hi:
        mid = lo + hi >> 1
        if is_enlightenment_possible(temple_positions, len(temple_positions), num_monks, mid):
            ans = mid
            hi = mid - 1
        else:
            lo = mid + 1
    return ans

