def min_quasibinary_representation(n: int) -> tuple[int, list[int]]:
    """"""
    s = []
    ans = 0
    while n > 0:
        m = int(''.join(['1' if d != '0' else '0' for d in str(n)]))
        n -= m
        if n >= 0:
            ans += 1
            s.append(m)
    return (ans, s)
