def simulate_chair_seating(n, m, passengers):
    INF = 10 * 20
    chairs = ['#'] * n

    def sit(s):
        if s == 'A':
            chairs[chairs.index('#')] = 'A'
        elif s == 'B':
            for i in range(n - 1, -1, -1):
                if chairs[i] == '#' and (i == 0 or chairs[i - 1] != 'A') and (i == n - 1 or chairs[i + 1] != 'A'):
                    chairs[i] = 'B'
                    break
            else:
                chairs[chairs.index('#')] = 'B'
        elif s == 'C':
            for i in range(n):
                if chairs[i] != '#':
                    if i + 1 <= n - 1 and chairs[i + 1] == '#':
                        chairs[i + 1] = 'C'
                        break
                    elif i - 1 >= 0 and chairs[i - 1] == '#':
                        chairs[i - 1] = 'C'
                        break
        else:
            score = [INF for _ in range(n)]
            dist = INF
            for i in range(n):
                if chairs[i] == '#':
                    dist += 1
                else:
                    dist = 0
                score[i] = dist
            dist = INF
            for i in range(n - 1, -1, -1):
                if chairs[i] == '#':
                    dist += 1
                else:
                    dist = 0
                score[i] = min(score[i], dist)
            high_score = max(score)
            chairs[score.index(high_score)] = 'D'
    first = passengers[0]
    if first in ['A', 'D']:
        chairs[0] = first
    elif first == 'B':
        chairs[-1] = 'B'
    else:
        chairs[n // 2] = 'C'
    for passenger in passengers[1:]:
        sit(passenger)
    return ''.join(chairs)

