def calculate_calendar_columns(month: int, first_day_weekday: int) -> int:
    days_in_month = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31]
    days = days_in_month[month - 1]
    days_left_in_first_week = 7 - first_day_weekday - 1
    full_weeks = (days - days_left_in_first_week) // 7
    remaining_days = (days - days_left_in_first_week) % 7
    if remaining_days == 0:
        return full_weeks + 1
    else:
        return full_weeks + 2

