def min_swaps_to_unbalance(S: str) -> int:
    n = len(S)
    dist = []
    for i in range(n):
        if S[i] != ')':
            dist.append(i + 1)
    prefix = [0]
    for i in range(len(dist)):
        prefix.append(prefix[-1] + dist[i])
    dist = dist[::-1]

    def answer():
        ans = float('inf')
        (o, c, ind) = (0, 0, 0)
        for i in range(n):
            req = o - c + 1
            while dist[-1] < i + 1:
                dist.pop()
                ind += 1
            if n // 2 - ind < req:
                break
            cal = prefix[ind + req] - prefix[ind] - req * i - req * (req + 1) // 2
            ans = min(ans, cal)
            if S[i] == '(':
                o += 1
            else:
                c += 1
        return ans
    return answer()

