def prime_factors_of_lcm(Arr, N):
    from math import gcd

    def lcm(a, b):
        return a * b // gcd(a, b)
    lc = Arr[0]
    for i in Arr:
        lc = lcm(lc, i)
    prime_factors = []
    d = 3
    while lc >= d:
        if lc % d == 0:
            while lc % d == 0:
                lc //= d
            prime_factors.append(d)
        d += 1
    return prime_factors

