    pivot.next = greater_than_pivot
    if less_than_pivot is None:
        return pivot
    sorted_head = less_than_pivot
    while less_than_pivot.next is not None:
        less_than_pivot = less_than_pivot.next
    less_than_pivot.next = pivot
    return sorted_head

