def calculate_mushroom_weights(n, x, y, p, weights):

    def mod(x):
        return (x % p + p) % p

    def matmul(a, b):
        n = len(a)
        c = [[0 for _ in range(n)] for _ in range(n)]
        for i in range(n):
            for j in range(n):
                for k in range(n):
                    c[i][j] = (c[i][j] + a[i][k] * b[k][j]) % p
        return c

    def matpow(b, p):
        n = len(b)
        res = [[0 if x != y else 1 for x in range(n)] for y in range(n)]
        while p:
            if p & 1:
                res = matmul(res, b)
            b = matmul(b, b)
            p >>= 1
        return res
    weights = sorted(weights)
    if n != 1:
        return weights[0]
    b = matpow([[1, -1], [0, 3]], x)
    sum0 = matmul([[weights[0] + weights[-1], sum(weights)], [0, 0]], b)[0][1]
    b = matpow([[0, 1], [1, 1]], x)
    maxa = matmul([[weights[-2], weights[-1]], [0, 0]], b)[0][1]
    b = matpow([[1, -1], [0, 3]], y)
    sum1 = matmul([[weights[0] + maxa, sum0], [0, 0]], b)[0][1]
    return mod(sum1)

