    b.sort(reverse=True)
    max_a = max(a)
    cnt = 1
    for damage in b:
        if H <= 0 or damage <= max_a:
            break
        H -= damage
        cnt += 1
    if H > 0:
        cnt += (H + max_a - 1) // max_a
