def is_contest_balanced(N, P, solvers):
    cakewalk_threshold = P / 2
    hard_threshold = P // 10
    cakewalk_count = 0
    hard_count = 0
    for participants in solvers:
        if participants >= cakewalk_threshold:
            cakewalk_count += 1
        elif participants <= hard_threshold:
            hard_count += 1
    return cakewalk_count == 1 and hard_count == 2

