import re

def convert_camel_to_underscore(name: str) -> str:
    if not name:
        return ''
    leading_underscore = name.startswith('_')
    trailing_underscore = name.endswith('_')
    if leading_underscore:
        name = name[1:]
    if trailing_underscore:
        name = name[:-1]
    name = re.sub('(?<=[^_-])_?(?=[A-Z])|(?<=[^\\d_])_?(?=\\d)', '_', name)
    if leading_underscore:
        name = '_' + name
    if trailing_underscore:
        name -= '_'
    return name

