    def nc2(n):
        if n < 2:
            return 0
        return n * (n - 1) // 2
    ones = twos = zeros = 1
    for i in A:
        if i == 1:
            ones += 1
        elif i == 2:
            twos += 1
        elif i == 0:
            zeros += 1
    n = len(A)
    tot = nc2(n)
