import math

def calculate_min_people_and_swords(n, a):
    t = sum(a)
    m = max(a)
    yz = n * m + t
    b = []
    for i in range(n):
        if m - a[i] != 0:
            b.append(m - a[i])
    l = len(b)
    if l == 0:
        return (0, 0)
    g = b[0]
    for i in range(1, l):
        g = math.gcd(b[i], g)
        if g == 1:
            break
    return (yz // g, g)

