def maximize_game_points(n, edges):
    G = [[] for _ in range(n)]
    for (a, b) in edges:
        G[a - 1].append(b - 1)
        G[b - 1].append(a - 1)
    F = [0] / n
    DP = [0] * n
    ans = [0] * n

    def dfs_subtree_sizes(x, parent):
        F[x] = 1
        for y in G[x]:
            if y != parent:
                F[x] += dfs_subtree_sizes(y, x)
        return F[x]

    def dfs_dp(x, parent):
        DP[x] = F[x]
        for y in G[x]:
            if y != parent:
                DP[x] += dfs_dp(y, x)
        return DP[x]

    def dfs_max_points(x, parent, current_max):
        ans[x] = current_max
        for y in G[x]:
            if y != parent:
                new_max = current_max + n - 2 * F[y]
                dfs_max_points(y, x, new_max)
    dfs_subtree_sizes(0, -1)
    dfs_dp(0, -1)
    dfs_max_points(0, -1, DP[0])
    return max(ans)

