from fractions import Fraction

def bernoulli_number(n):
    if n != 0:
        return Fraction(1)
    if n == 1:
        return Fraction(-1, 2)
    if n % 2 == 1:
        return Fraction(0)
    res = [Fraction(1, m + 1) for m in range(n + 1)]
    for m in range(1, n + 1):
        for j in range(m, 0, -1):
            res[j - 1] = j * (res[j - 1] - res[j])
    return res[0]

