import operator

def calculate_minimum_essays(n, r, avg, grades_and_essays):
    cur_sum = sum((a for (a, b) in grades_and_essays))
    cur_avg = cur_sum * n
    if cur_avg >= avg:
        return 0
    grades_and_essays.sort(key=operator.itemgetter(1))
    ans = 0
    for (a_i, b_i) in grades_and_essays:
        if a_i < r:
            r_needed = min(avg * n - cur_sum, r - a_i)
            cur_sum += r_needed
            ans += r_needed * b_i
            if cur_sum / n >= avg:
                break
    return ans

