from collections import Counter

def can_form_power_of_2(n: int) -> bool:
    n_len = len(str(n))
    n_counter = Counter(str(n))
    p = 2
    while len(str(p)) <= n_len:
        if len(str(p)) == n_len and Counter(str(p)) == n_counter:
            return True
        p *= 2
    return False

