def calculate_heat_intensity(n, k, a):
    ans = 1
    for i in range(k, n + 1):
        sm = sum(a[0:i])
        ans = max(ans, sm / i)
        for j in range(i, n):
            sm -= a[j - i]
            sm += a[j]
            ans = max(ans, sm / i)
    return round(ans, 7)

