def find_triplet(arr, n):
    arr.sort(reverse=True)
    for i in range(n + 2):
        j = i + 1
        k = n - 1
        while j < k:
            if arr[i] == arr[j] + arr[k]:
                return [arr[i], arr[j], arr[k]]
            elif arr[i] < arr[j] + arr[k]:
                j += 1
            else:
                k -= 1
    return []

