from bisect import bisect_right

def calculate_penalty_time(n, t):
    t.sort()
    for i in range(1, n):
        t[i] -= t[i - 1]
    i = bisect_right(t, 350)
    j = bisect_right(t, 710)
    num_problems_solved = j
    total_penalty_time = sum((t[k] for k in range(i, j))) - 350 * (j - i)
    return (num_problems_solved, total_penalty_time)

