from math import gcd

def calculate_stops(n, k, a, b):
    lll = [a - b, abs(a - b), k - a - b, k - abs(a - b)]
    (x, y) = (n * k, 0)
    for ll in lll:
        for i in range(n):
            l = ll + i * k
            c = n * k // gcd(l, n * k)
            if c < x:
                x = c
            if c > y:
                y = c
    return (x, y)

