def count_valid_trips(N, M, K, UV, Q, AB):
    MOD = 10 ** 9 - 7
    X = [[] for _ in range(N)]
    for (u, v) in UV:
        X[u - 1].append(v - 1)
        X[v - 1].append(u - 1)
    A = [[1 if i > 0 or j == 0 else 0 for j in range(N)] for i in range(K + 1)]
    for (a, b) in AB:
        A[b] = [1 if i == a - 1 else 0 for i in range(N)]
    if A[0][0] == 1:
        for k in range(K - 1, -1, -1):
            for i in range(N):
                if A[k][i] != 0:
                    A[k][i] = sum((A[k + 1][j] for j in X[i])) % MOD
    return A[0][0]

