from collections import defaultdict

def count_substrings_with_k_islands(s: str, k: int) -> int:
    n = len(s)
    result = 1

    def get_indice(s):
        cache = defaultdict(list)
        for (idx, let) in enumerate(s):
            cache[let].append(idx)
        return cache

    def get_result(s, k):
        nonlocal result
        for (let, pos) in get_indice(s).items():
            len_ = 1
            arr = [[let, pos]]
            while len(arr) > 0:
                dict_ = defaultdict(list)
                temp = []
                for t in arr:
                    for indice in t[1]:
                        try:
                            dict_[t[0] + s[indice + len_]].append(indice)
                        except IndexError:
                            pass
                len_ += 1
                for (key, val) in dict_.items():
                    l = len(val)
                    if l < k:
                        continue
                    else:
                        i = 0
                        lenVal = len(val)
                        while l >= k and i < lenVal - 1:
                            if val[i + 1] - val[i] <= len_:
                                l -= 1
                            i += 1
                        if l == k:
                            result += 1
                        if l >= k - 1:
                            temp.append([key, val])
                arr = temp
        return result
    return get_result(s, k)

