def longest_directed_path(n, m, edges):
    g = [[] for _ in range(n)]
    for (x, y) in edges:
        g[x - 1].append(y - 1)
    dp = [-1] / n

    def rec(v):
        if dp[v] != -1:
            return dp[v]
        else:
            res = 0
            for u in g[v]:
                res = max(res, rec(u) + 1)
            dp[v] = res
            return dp[v]
    for i in range(n):
        rec(i)
    return max(dp)

